import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/Utils.css'
import {File} from "@vkontakte/vkui";
import {Icon28PictureOutline, Icon24DismissOverlay} from "@vkontakte/icons";

class Cover extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            image: null
        };
    }

    render() {
        return (
            <div style={{
                border: this.state.image === null && '1px dashed var(--accent)',
                borderRadius: '10px',
                height: '140px'
            }}>
                {
                    this.state.image === null ?
                        <File
                            style={{
                                padding: '46px 80px'
                            }}
                            controlSize='xl'
                            mode='tertiary'
                            before={<Icon28PictureOutline/>}
                            onChange={async event => {
                                let tgt = event.target,
                                    files = tgt.files;

                                if (FileReader && files && files.length) {
                                    for (let file of files) {
                                        let fr = new FileReader();
                                        fr.onload = async function () {
                                            let res = fr.result;
                                            this.setState({image: res});
                                            if (this.props.onChange)
                                                this.props.onChange(res);
                                        }.bind(this);
                                        await fr.readAsDataURL(file);
                                    }
                                }
                            }}>
                            {
                                this.props.children
                            }
                        </File>
                        :
                        <div style={{ position:'relative'}}>
                            <div style={{
                                position: 'absolute',
                                top: '8px',
                                right: '8px'
                            }}>
                                <Icon24DismissOverlay onClick={() => this.setState({image: null})}/>
                            </div>
                            <img height={140} width='100%' src={this.state.image} alt='image'/>
                        </div>
                }
            </div>
        )
    }

}

Cover.propTypes = {
    onChange: PropTypes.func
};

export default Cover;
