import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';

import {Panel, PanelHeader, PanelHeaderBack, FormLayout, Input, Textarea, Div, Select} from '@vkontakte/vkui';

import {} from '@vkontakte/icons';
import Cover from "../components/Cover";

class CreateDonationTarget extends React.Component {
    render() {
        const App = this.props.app;

        return (
            <Panel id={this.props.id}>
                <PanelHeader left={<PanelHeaderBack onClick={App.back}/>}>Целевой сбор</PanelHeader>
                <Div>
                    <Cover>Загрузить обложку</Cover>
                </Div>
                <FormLayout>
                    <Input top='Название сбора' placeholder='Название сбора' />
                    <Input top='Сумма, ₽' placeholder='Сколько нужно собрать?' />
                    <Input top='Цель' placeholder='Например, лечение человека' />
                    <Textarea top='Описание' placeholder='На что пойдут деньги и как они кому-то помогут?' />
                    <Select top='Куда получать деньги' placeholder="Выберите пол">
                        <option value="m">Мужской</option>
                        <option value="f">Женский</option>
                    </Select>
                </FormLayout>
            </Panel>
        );
    }
}

CreateDonationTarget.propTypes = {
    id: PropTypes.string.isRequired,
    app: PropTypes.object
};

export default CreateDonationTarget;
