import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/Comment.css';

import {Icon16LikeOutline} from '@vkontakte/icons';
import {Avatar, SimpleCell, Subhead} from "@vkontakte/vkui";

class Comment extends React.Component {

    render() {
        return (
            <SimpleCell className='comment__' before={<Avatar size={36} src={this.props.avatar} />} after={<Icon16LikeOutline />} description={this.props.text}>
                <Subhead weight='bold'>Игорь Фёдоров</Subhead> {this.props.time}
            </SimpleCell>
        );
    }
}

Comment.propTypes = {
    avatar: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    text: PropTypes.string.isRequired,
    time: PropTypes.string.isRequired
};

export default Comment;
