import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/Utils.css'

class Cover extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            image: null
        };
    }

    render() {
        return (
            this.state.image === null ?
                <div
                    style={{
                        border: '1px dashed var(--accent)',
                        borderRadius: '10px',
                        color: 'var(--accent)'
                    }}
                    onClick={() => {
                        console.log('click');
                        this.uploadImage.click();
                    }}>
                    <input accept='*' type='image' style={{display: 'none'}} onChange={async event => {
                        console.log('event');
                        
                    }}
                           ref={ref => this.uploadImage = ref}/>
                    <div style={{ padding: '56px 82px' }} className='centered'>
                        {
                            this.props.children
                        }
                    </div>
                </div>
                :
                <div>

                </div>
        )
    }

}

Cover.propTypes = {};

export default Cover;
