import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/UserWall.css';

import {Icon16MoreHorizontal} from '@vkontakte/icons';
import {Avatar, Caption, SimpleCell, Subhead, Text} from "@vkontakte/vkui";

class UserWall extends React.Component {

    render() {
        return (
            <SimpleCell disabled className='user_wall__' before={<Avatar size={36} src={this.props.avatar}/>}
                        after={<Icon16MoreHorizontal fill='var(--icon_outline_medium)'/>} description={
                <Subhead weight='bold'>{this.props.time}</Subhead>
            }>
                <span className={'user_wall_top__'}>
                    <Text weight='medium'>{this.props.name}</Text>
                </span>
            </SimpleCell>
        );
    }
}

UserWall.propTypes = {
    avatar: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    time: PropTypes.string.isRequired
};

export default UserWall;
