import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/Progress.css';

import {Progress} from '@vkontakte/vkui';

import {getRandomInt, shortIntegers} from '../Utils';

const progress_id = getRandomInt(0, 123456789);

class CustomProgress extends React.Component {
    componentDidMount() {
        return;
        let progress = document.getElementById(progress_id),
            progressIn = progress.getElementsByClassName('Progress__in')[0],
            span1 = document.createElement('div'),
            span2 = document.createElement('div');
        progress.style.color = 'white';
        progress.style.display = 'flex';
        span1.innerText = `${shortIntegers(this.props.valueOf)} ${(this.props.suffix ? this.props.suffix : '')}`;
        progress.appendChild(span1);
        progress.style.background = this.props.colors[1];
        span2.innerText = shortIntegers(this.props.value) + (this.props.suffix ? ' ' + this.props.suffix : '');
        span2.style.textAlign = 'right';
        span2.style.marginRight = '8px';
        span2.style.paddingLeft = '12px';
        progressIn.appendChild(span2);
        progressIn.style.background = this.props.colors[0];
    }

    render() {
        let l = <Progress id={progress_id} value={this.props.value / this.props.valueOf * 100}/>;
        return (
            <div className='progress__' style={{background: this.props.colors[1]}}>
                <div className='progress_value__' style={{
                    width: `${this.props.value / this.props.valueOf * 100}%`,
                    background: this.props.colors[0]
                }}>{`${shortIntegers(this.props.value)} ${(this.props.suffix ? this.props.suffix : '')}`}</div>
                {`${shortIntegers(this.props.valueOf)} ${(this.props.suffix ? this.props.suffix : '')}`}
            </div>
        );
    }
}

CustomProgress.propTypes = {
    value: PropTypes.number.isRequired,
    valueOf: PropTypes.number.isRequired,
    colors: PropTypes.array.isRequired,
    suffix: PropTypes.string
};

export default CustomProgress;
