import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';

import {Panel, PanelHeader, PanelHeaderBack, Div, Title} from '@vkontakte/vkui';

import {} from '@vkontakte/icons';

class Donation extends React.Component {

    render() {
        const App = this.props.app;

        return (
            <Panel id={this.props.id} style={{ position: 'relative' }}>
                <div style={{
                    height: '140px',
                    position: 'absolute',
                    left: 0,
                    right: 0,
                    top: 0,
                    bottom: 0,
                    backgroundImage: `url(${App.state.header})`,
                    backgroundPosition: 'center center',
                    backgroundSize: 'cover',
                    backgroundRepeat: 'no-repeat'
                }}>
                    <PanelHeaderBack onClick={App.back} style={{position: 'absolute', left: 12, top: 0, bottom: 0, color: 'white'}}/>
                </div>
                <Div>
                    <Title level={1} weight='bold'></Title>
                </Div>
            </Panel>
        );
    }
}

Donation.propTypes = {
    id: PropTypes.string.isRequired,
    app: PropTypes.object
};

export default Donation;
