import React from 'react';
import PropTypes from 'prop-types';
import '@vkontakte/vkui/dist/vkui.css';
import '../css/Progress.css';

import {Progress} from '@vkontakte/vkui';

import {getRandomInt, shortIntegers} from '../Utils';

const progress_id = getRandomInt(0, 123456789);

class CustomProgress extends React.Component {
    componentDidMount() {
        let progress = document.getElementById(progress_id),
            progressIn = progress.getElementsByClassName('Progress__in')[0];
        progress.appendChild(`<span>${shortIntegers(this.props.valueOf)} ${( this.props.suffix ? this.props.suffix : '')}</span>`);
        progress.style.background = this.props.colors[1];
        progressIn.innerText = shortIntegers(this.props.value) + ( this.props.suffix ? ' ' + this.props.suffix : '');
        progressIn.style.background = this.props.colors[0];
    }

    render() {
        return (
            <Progress id={progress_id} value={this.props.value / this.props.valueOf * 100}/>
        );
    }
}

CustomProgress.propTypes = {
    value: PropTypes.number.isRequired,
    valueOf: PropTypes.number.isRequired,
    colors: PropTypes.array.isRequired,
    suffix: PropTypes.string
};

export default CustomProgress;
